import React, { Component } from "react";
import Counter from "./counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 4 }, //each counter component
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  constructor() {
    super();
    this.handleReset = this.handleReset.bind(this); //need to bind functions for proper use if called by a child
    this.handleIncrement = this.handleIncrement.bind(this);
    //this.handleReset = this.handleDelete.bind(this);
  }

  handleIncrement(counter) {
    const counters = [...this.state.counters]; //clone array
    const index = counters.indexOf(counter); //get index of counter
    counters[index] = { ...counter }; //clone counter object that is the received argument
    counters[index].value++; //increment value
    this.setState({ counters }); //set the new counter object
  }

  //handleDelete(counter) {}

  handleReset() {
    const counters = this.state.counters.map((c) => {
      c.value = 0; //for loop where for c in counters
      return c;
    });
    this.setState({ counters });
  }
  render() {
    return (
      <div>
        <button
          onClick={this.handleReset} //when button is clicked reset
          className="btn btn-primary btn-sm m-2" //button styling
        >
          Reset
        </button>
        {this.state.counters.map((counter) => (
          <Counter
            key={counter.id}
            onIncrement={this.handleIncrement} //call increment function from counter.jsx
            onDelete={this.handleDelete}
            counter={counter}
          />
        ))}
      </div>
    );
  }
}

export default Counters;
